nk&#x27;,                            &#x27;js&#x27;: &#x27;script&#x27;                        &#125;)[resource.split(&#x27;.&#x27;).pop()],                        () =&gt; --unloadedResourceCount ? null : callback()                    );                &#125;);            &#125;,            createDplayers: function (sources, callback) &#123;                for (let i = 0; i &lt; sources.length; i++) &#123;                    const child = document.createElement(&#x27;div&#x27;);                    const src = sources[i].getAttribute(&#x27;src&#x27;);                    sources[i].parentNode.insertBefore(child, sources[i]);                    sources[i].style.display = &#x27;none&#x27;;                    const type = src.split(&#x27;.&#x27;).pop();                    const option = &#123; url: src &#125;;                    type === &#x27;flv&#x27; ? option.type = type : null;                    const dplayer = new DPlayer(&#123; container: child, preload: &#x27;none&#x27;, autoplay: false, screenshot: false, video: option &#125;);                &#125;                if (typeof callback === &#x27;function&#x27;) callback();            &#125;        &#125;;        const mirages = &#123;            isMirages: function () &#123; return Mirages || false &#125;,            fixVideoSize: function (length) &#123;                let outerTimer = false;                const outerInterval = setInterval(() =&gt; &#123;                    if (outerTimer) return;                    const videos = document.getElementsByTagName(&#x27;video&#x27;);                    if (videos.length === length) &#123;                        const dplayerWraps = document.querySelectorAll(&#x27;.dplayer-video-wrap&#x27;);                        for (let i = 0; i &lt; length; i++) &#123;                            const videoContainers = dplayerWraps[i].querySelectorAll(&#x27;.video-container.video-4-3&#x27;);                            if (videoContainers.length) &#123;                                videoContainers[0].style = &#x27;position: initial;&#x27;;                                videoContainers[0].className = &#x27;video-container video-16-9&#x27;;                                console.log(&#x27;video-4-3 fixed.&#x27;);                            &#125; else &#123;                                const videoContainer = document.createElement(&#x27;div&#x27;);                                videoContainer.style = &#x27;position: initial;&#x27;;                                videoContainer.className = &#x27;video-container video-16-9&#x27;;                                videoContainer.appendChild(videos[i]);                                dplayerWraps[i].appendChild(videoContainer);                                console.log(&#x27;video-16-9 inserted.&#x27;);                                const targetNode = videoContainer;                                const config = &#123; childList: true &#125;;                                const callback = (mutationsList, observer) =&gt; &#123;                                    const newVideoContainers = videoContainer.querySelectorAll(                                        &#x27;.video-container.video-4-3&#x27;);                                    if (newVideoContainers.length) &#123;                                        newVideoContainers[0].className = &#x27;&#x27;;                                        console.log(&#x27;auto inserted video-4-3 fixed.&#x27;);                                        observer.disconnect();                                    &#125;                                &#125;;                                const observer = new MutationObserver(callback);                                observer.observe(targetNode, config);                                setTimeout(() =&gt; observer.disconnect(), 1000 * 120);                            &#125;                        &#125;                        outerTimer = true;                        clearInterval(outerInterval);             